import './App.css';
import { Routes, Route } from 'react-router-dom'
import routes from './pages/router'
import loader from './assets/images/loader/loader.gif';
function App() {

    setInterval(function(){
     document.getElementById("loader").style.display="none";  
     document.getElementById("root_page").style.display=""; 
    },3000);

    return (
        <>

            <div id="loader" >
                <img src={loader} alt="Loader" className="loader_image"></img>
            </div>
            <div id="root_page" style={{display: 'none'}}>
                <Routes>
                    {
                        routes.map((data, index) => (
                            <Route exact={true} path={data.path} element={data.component} key={index} />
                        ))
                    }
                </Routes>
            </div>
        </>

    );
}

export default App;
